import React, { useState } from 'react';

const TourCalendar = () => {
    const [viewDate, setViewDate] = useState(new Date());

    // Mock tour events with start and end dates
    const tourEvents = [
        { id: 1, title: 'Istanbul Magic', start: 3, duration: 5, color: '#e3f2fd', textColor: '#1976d2' },
        { id: 2, title: 'Dubai Expo', start: 12, duration: 4, color: '#f1f8e9', textColor: '#388e3c' },
        { id: 3, title: 'Antalya Summer', start: 8, duration: 7, color: '#fff3e0', textColor: '#f57c00' },
        { id: 4, title: 'Samarkand Silk', start: 22, duration: 3, color: '#f3e5f5', textColor: '#7b1fa2' },
        { id: 5, title: 'Khiva Historic', start: 18, duration: 5, color: '#e0f2f1', textColor: '#00796b' },
    ];

    const monthName = viewDate.toLocaleString('default', { month: 'long' });
    const year = viewDate.getFullYear();

    const daysInMonth = 31; // Simplified for mockup
    const calendarDays = Array.from({ length: daysInMonth }, (_, i) => i + 1);

    return (
        <div className="container-fluid">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <div>
                    <h3 className="fw-bold m-0">Tour Schedule</h3>
                    <p className="text-muted small">Visual calendar for arrivals and departures</p>
                </div>
                <div className="d-flex gap-2 align-items-center bg-white p-2 rounded-pill shadow-sm">
                    <button className="btn btn-sm btn-light rounded-circle"><i className="fa fa-chevron-left"></i></button>
                    <span className="fw-bold px-3">{monthName} {year}</span>
                    <button className="btn btn-sm btn-light rounded-circle"><i className="fa fa-chevron-right"></i></button>
                </div>
            </div>

            <div className="card border-0 shadow-sm rounded-4 bg-white overflow-hidden">
                {/* Calendar Header */}
                <div className="row g-0 border-bottom text-center bg-light bg-opacity-50">
                    {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map(day => (
                        <div key={day} className="col py-3 fw-bold small text-muted border-end last-child-border-0">
                            {day}
                        </div>
                    ))}
                </div>

                {/* Calendar Grid */}
                <div className="row g-0 position-relative" style={{ minHeight: '600px' }}>
                    {calendarDays.map((day, index) => (
                        <div key={day} className="col-calendar-day border-end border-bottom p-2" style={{ width: '14.28%', height: '120px' }}>
                            <span className={`small fw-bold ${day === 12 ? 'bg-primary text-white rounded-circle p-1 px-2' : 'text-muted'}`}>{day}</span>

                            {/* Render Tour Bars */}
                            <div className="mt-2 d-grid gap-1">
                                {tourEvents.map(tour => {
                                    if (tour.start === day) {
                                        return (
                                            <div key={tour.id}
                                                className="tour-bar px-2 py-1 rounded-1 x-small fw-bold shadow-sm animate-slideIn"
                                                style={{
                                                    backgroundColor: tour.color,
                                                    color: tour.textColor,
                                                    width: `calc(${tour.duration * 100}% + ${(tour.duration - 1) * 1}px)`,
                                                    zIndex: 10,
                                                    position: 'absolute',
                                                    left: `${(index % 7) * 14.28 + 0.5}%`,
                                                    whiteSpace: 'nowrap',
                                                    overflow: 'hidden',
                                                    textOverflow: 'ellipsis',
                                                    marginTop: `${(tour.id % 3) * 25}px`
                                                }}>
                                                <i className="fa fa-plane-arrival me-1"></i> {tour.title}
                                            </div>
                                        );
                                    }
                                    return null;
                                })}
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* Legend */}
            <div className="mt-4 d-flex gap-4">
                <div className="d-flex align-items-center small">
                    <div className="bg-primary rounded-circle me-2" style={{ width: '10px', height: '10px' }}></div>
                    <span className="text-muted">Today</span>
                </div>
                <div className="d-flex align-items-center small">
                    <i className="fa fa-plane-arrival text-success me-2"></i>
                    <span className="text-muted">New Arrival</span>
                </div>
            </div>

            <style>{`
                .col-calendar-day {
                    transition: background 0.2s;
                    position: relative;
                }
                .col-calendar-day:hover {
                    background-color: #f8f9fa;
                }
                .last-child-border-0:last-child {
                    border-right: 0 !important;
                }
                .x-small {
                    font-size: 10px;
                }
                .tour-bar {
                    border-left: 3px solid currentColor;
                    cursor: pointer;
                    transition: transform 0.2s;
                }
                .tour-bar:hover {
                    transform: scale(1.02);
                    filter: brightness(0.95);
                }
                .animate-slideIn {
                    animation: slideIn 0.5s ease-out;
                }
                @keyframes slideIn {
                    from { opacity: 0; transform: translateX(-10px); }
                    to { opacity: 1; transform: translateX(0); }
                }
            `}</style>
        </div>
    );
};

export default TourCalendar;
