import React from 'react';
import { Link, Outlet, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const AgentLayout = () => {
    const location = useLocation();
    const { logout, user } = useAuth();
    const navigate = useNavigate();

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    const menuItems = [
        { path: '/agent', label: 'Dashboard', icon: 'fa-home' },
        { path: '/agent/search/tours', label: 'Search Tours', icon: 'fa-map-marked-alt' },
        { path: '/agent/search/hotels', label: 'Search Hotels', icon: 'fa-hotel' },
        { path: '/agent/search/flights', label: 'Search Flights', icon: 'fa-plane' },
        { path: '/agent/history', label: 'Booking History', icon: 'fa-history' },
        { path: '/agent/settings', label: 'Settings', icon: 'fa-cog' },
    ];

    return (
        <div className="agent-container d-flex" style={{ minHeight: '100vh', backgroundColor: '#f8fafc' }}>
            {/* Sidebar */}
            <div className="agent-sidebar bg-white border-end d-flex flex-column" style={{ width: '280px', position: 'fixed', height: '100vh', zIndex: 1000 }}>
                <div className="p-4 border-bottom text-center">
                    <h4 className="text-primary m-0 fw-bold">
                        <i className="fa fa-globe me-2"></i>Agent Cabinet
                    </h4>
                    <p className="text-muted small mb-0 mt-1">{user?.officialName || 'B2B Partner'}</p>
                </div>

                <div className="flex-grow-1 py-4 px-3 overflow-auto">
                    {menuItems.map((item) => (
                        <Link
                            key={item.path}
                            to={item.path}
                            className={`d-flex align-items-center p-3 mb-2 rounded-3 text-decoration-none transition-all ${location.pathname === item.path
                                    ? 'bg-primary text-white shadow-sm'
                                    : 'text-dark hover-bg-light'
                                }`}
                        >
                            <div className={`p-2 rounded-2 me-3 ${location.pathname === item.path ? 'bg-white bg-opacity-25' : 'bg-light text-primary'}`}>
                                <i className={`fa ${item.icon}`} style={{ width: '20px', textAlign: 'center' }}></i>
                            </div>
                            <span className="fw-semibold small">{item.label}</span>
                        </Link>
                    ))}
                </div>

                <div className="p-4 border-top">
                    <button onClick={handleLogout} className="btn btn-outline-danger w-100 rounded-pill py-2 small fw-bold">
                        <i className="fa fa-sign-out-alt me-2"></i>Logout
                    </button>
                    <Link to="/" className="d-block text-center mt-3 text-muted small text-decoration-none">
                        <i className="fa fa-arrow-left me-1"></i> Public Site
                    </Link>
                </div>
            </div>

            {/* Main Content */}
            <div className="agent-main-content flex-grow-1" style={{ marginLeft: '280px' }}>
                {/* Top Header */}
                <header className="bg-white border-bottom py-3 px-4 d-flex justify-content-between align-items-center sticky-top">
                    <div className="d-flex align-items-center">
                        <h5 className="m-0 fw-bold text-dark">
                            {menuItems.find(i => i.path === location.pathname)?.label || 'Agent Panel'}
                        </h5>
                    </div>

                    <div className="d-flex align-items-center gap-3">
                        <div className="text-end d-none d-md-block">
                            <span className="d-block fw-bold small text-dark">{user?.headName || user?.name}</span>
                            <span className="text-success x-small fw-bold px-2 py-0 bg-success bg-opacity-10 rounded-pill">Active Partner</span>
                        </div>
                        <img
                            src={`https://ui-avatars.com/api/?name=${user?.officialName || 'Agent'}&background=30c39e&color=fff`}
                            className="rounded-circle border shadow-sm"
                            style={{ width: '40px', height: '40px' }}
                            alt="Avatar"
                        />
                    </div>
                </header>

                {/* Page Content */}
                <main className="p-4">
                    <div className="container-fluid p-0">
                        <Outlet />
                    </div>
                </main>
            </div>

            <style>{`
                .transition-all {
                    transition: all 0.2s ease;
                }
                .hover-bg-light:hover {
                    background-color: #f1f5f9;
                    color: #0d6efd !important;
                }
                .x-small {
                    font-size: 10px;
                    letter-spacing: 0.5px;
                    text-transform: uppercase;
                }
                .bg-primary {
                    background-color: #0d6efd !important;
                }
                .text-primary {
                    color: #0d6efd !important;
                }
            `}</style>
        </div>
    );
};

export default AgentLayout;
