import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Select from 'react-select';
import DatePicker from 'react-datepicker';
import { toursAPI } from '../../services/api';
import 'react-datepicker/dist/react-datepicker.css';

const B2BCreateTour = () => {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState(false);

    const [formData, setFormData] = useState({
        title: '',
        departureTown: null,
        tour: null,
        state: null,
        departureDate: new Date(),
        nightsFrom: 7,
        nightsTill: 14,
        adults: 2,
        children: 0,
        priceAdult: '',
        priceChild: '',
        agencyCommission: '',
        town: null,
        category: null,
        hotels: [],
        mealType: null,
        isPromotion: false,
        description: '',
        images: []
    });

    // Mock Options
    const departureCities = [
        { value: 'TAS', label: 'Tashkent' },
        { value: 'SAM', label: 'Samarkand' },
        { value: 'DXB', label: 'Dubai' }
    ];

    const categoryOptions = [
        { value: '5', label: '5* Stars' },
        { value: '4', label: '4* Stars' },
        { value: '3', label: '3* Stars' }
    ];

    const mealTypeOptions = [
        { value: 'AI', label: 'All Inclusive (AI)' },
        { value: 'UAI', label: 'Ultra All Inclusive (UAI)' },
        { value: 'FB', label: 'Full Board (FB)' },
        { value: 'HB', label: 'Half Board (HB)' }
    ];

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const tourData = {
                ...formData,
                departureTown: formData.departureTown?.label,
                toCity: formData.tour?.label,
                state: formData.state?.label,
                category: formData.category?.value,
                mealType: formData.mealType?.value,
                hotels: formData.hotels.map(h => h.label),
                tourType: 'B2B',
                status: 'Active'
            };

            await toursAPI.create(tourData);
            setSuccess(true);
            setTimeout(() => navigate('/admin/tours'), 2000);
        } catch (err) {
            setError(err.message || 'Failed to create B2B tour');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container-fluid py-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <div>
                    <h3 className="fw-bold m-0">Create B2B Tour</h3>
                    <p className="text-muted small">Specialized high-density form for partner packages</p>
                </div>
                <button className="btn btn-outline-secondary btn-sm rounded-pill px-3" onClick={() => navigate('/admin/tours')}>
                    <i className="fa fa-arrow-left me-2"></i>Back to Tours
                </button>
            </div>

            {error && <div className="alert alert-danger rounded-4 shadow-sm mb-4">{error}</div>}
            {success && <div className="alert alert-success rounded-4 shadow-sm mb-4">Tour created successfully! Redirecting...</div>}

            <form onSubmit={handleSubmit} className="row g-4">
                {/* Main Section */}
                <div className="col-lg-12">
                    <div className="card border-0 shadow-sm rounded-4 p-4 bg-white">
                        <h5 className="fw-bold mb-4 border-bottom pb-3"><i className="fa fa-info-circle me-2 text-primary"></i>Core Information</h5>
                        <div className="row g-3">
                            <div className="col-md-12">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Tour Title</label>
                                <input
                                    type="text"
                                    className="form-control form-control-sm bg-light border-0 py-2 fw-bold"
                                    placeholder="e.g. Egypt - Sharm El Sheikh Autumn Promo"
                                    value={formData.title}
                                    onChange={e => setFormData({ ...formData, title: e.target.value })}
                                    required
                                />
                            </div>
                            <div className="col-md-3">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Departure Town</label>
                                <Select
                                    options={departureCities}
                                    styles={customStyles}
                                    value={formData.departureTown}
                                    onChange={val => setFormData({ ...formData, departureTown: val })}
                                />
                            </div>
                            <div className="col-md-3">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Destination (Tour)</label>
                                <Select
                                    options={[{ value: 'EGY', label: 'Egypt' }, { value: 'TUR', label: 'Turkey' }]}
                                    styles={customStyles}
                                    value={formData.tour}
                                    onChange={val => setFormData({ ...formData, tour: val })}
                                />
                            </div>
                            <div className="col-md-3">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">State / Region</label>
                                <Select
                                    options={[{ value: 'SHM', label: 'Sharm El Sheikh' }, { value: 'HRG', label: 'Hurghada' }]}
                                    styles={customStyles}
                                    value={formData.state}
                                    onChange={val => setFormData({ ...formData, state: val })}
                                />
                            </div>
                            <div className="col-md-3">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Departure From</label>
                                <div className="input-group input-group-sm">
                                    <span className="input-group-text bg-light border-0"><i className="fa fa-calendar-alt text-muted small"></i></span>
                                    <DatePicker
                                        selected={formData.departureDate}
                                        onChange={date => setFormData({ ...formData, departureDate: date })}
                                        className="form-control bg-light border-0 small"
                                        dateFormat="dd.MM.yyyy"
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Logistics & Pricing Section */}
                <div className="col-lg-8">
                    <div className="card border-0 shadow-sm rounded-4 p-4 bg-white h-100">
                        <h5 className="fw-bold mb-4 border-bottom pb-3"><i className="fa fa-cog me-2 text-primary"></i>Logistics & Pricing</h5>
                        <div className="row g-3">
                            <div className="col-md-6">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Nights (From / Till)</label>
                                <div className="d-flex gap-2">
                                    <input type="number" className="form-control form-control-sm bg-light border-0 py-2" placeholder="Min" value={formData.nightsFrom} onChange={e => setFormData({ ...formData, nightsFrom: e.target.value })} />
                                    <input type="number" className="form-control form-control-sm bg-light border-0 py-2" placeholder="Max" value={formData.nightsTill} onChange={e => setFormData({ ...formData, nightsTill: e.target.value })} />
                                </div>
                            </div>
                            <div className="col-md-6">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Guests (Adult / Child)</label>
                                <div className="d-flex gap-2">
                                    <input type="number" className="form-control form-control-sm bg-light border-0 py-2" placeholder="Adults" value={formData.adults} onChange={e => setFormData({ ...formData, adults: e.target.value })} />
                                    <input type="number" className="form-control form-control-sm bg-light border-0 py-2" placeholder="Children" value={formData.children} onChange={e => setFormData({ ...formData, children: e.target.value })} />
                                </div>
                            </div>
                            <div className="col-md-4">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Adult Price ($)</label>
                                <input type="number" className="form-control form-control-sm bg-light border-0 py-2 fw-bold text-primary" placeholder="0.00" value={formData.priceAdult} onChange={e => setFormData({ ...formData, priceAdult: e.target.value })} />
                            </div>
                            <div className="col-md-4">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Child Price ($)</label>
                                <input type="number" className="form-control form-control-sm bg-light border-0 py-2 fw-bold text-primary" placeholder="0.00" value={formData.priceChild} onChange={e => setFormData({ ...formData, priceChild: e.target.value })} />
                            </div>
                            <div className="col-md-4">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Agency Commission ($)</label>
                                <input type="number" className="form-control form-control-sm bg-primary bg-opacity-10 border-0 py-2 fw-bold text-success" placeholder="Commission" value={formData.agencyCommission} onChange={e => setFormData({ ...formData, agencyCommission: e.target.value })} />
                            </div>
                            <div className="col-12 mt-4">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Short Description</label>
                                <textarea
                                    className="form-control form-control-sm bg-light border-0 py-2"
                                    rows="4"
                                    placeholder="Brief summary for search results..."
                                    value={formData.description}
                                    onChange={e => setFormData({ ...formData, description: e.target.value })}
                                ></textarea>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Hotel & Services Section */}
                <div className="col-lg-4">
                    <div className="card border-0 shadow-sm rounded-4 p-4 bg-white h-100">
                        <h5 className="fw-bold mb-4 border-bottom pb-3"><i className="fa fa-hotel me-2 text-primary"></i>Hotel & Services</h5>
                        <div className="row g-3">
                            <div className="col-12">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Hotel Category</label>
                                <Select
                                    options={categoryOptions}
                                    styles={customStyles}
                                    value={formData.category}
                                    onChange={val => setFormData({ ...formData, category: val })}
                                />
                            </div>
                            <div className="col-12">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Meal Type</label>
                                <Select
                                    options={mealTypeOptions}
                                    styles={customStyles}
                                    value={formData.mealType}
                                    onChange={val => setFormData({ ...formData, mealType: val })}
                                />
                            </div>
                            <div className="col-12">
                                <label className="x-small text-muted fw-bold text-uppercase mb-1">Property Selection</label>
                                <Select
                                    isMulti
                                    options={[
                                        { value: 'H1', label: 'Rixos Premium Saadiyat' },
                                        { value: 'H2', label: 'Hilton Fujairah Resort' }
                                    ]}
                                    styles={customStyles}
                                    value={formData.hotels}
                                    onChange={val => setFormData({ ...formData, hotels: val })}
                                />
                            </div>
                            <div className="col-12 mt-4">
                                <div className="form-check form-switch p-3 bg-light rounded-3 border">
                                    <input
                                        className="form-check-input ms-0 me-2"
                                        type="checkbox"
                                        id="promoSwitch"
                                        checked={formData.isPromotion}
                                        onChange={e => setFormData({ ...formData, isPromotion: e.target.checked })}
                                    />
                                    <label className="form-check-label fw-bold text-muted small" htmlFor="promoSwitch">MARK AS PROMOTION</label>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="col-12 text-end">
                    <div className="py-3 px-4 bg-white border rounded-pill d-inline-block shadow-sm mb-4">
                        <span className="small text-muted me-3">Final Action:</span>
                        <button type="submit" className="btn btn-primary rounded-pill px-5 fw-bold" disabled={loading}>
                            {loading ? <><span className="spinner-border spinner-border-sm me-2"></span>Creating...</> : 'CREATE & PUBLISH B2B TOUR'}
                        </button>
                    </div>
                </div>
            </form>

            <style>{`
                .x-small { font-size: 11px; letter-spacing: 0.5px; }
                .text-primary { color: #30c39e !important; }
                .btn-primary { background-color: #30c39e; border-color: #30c39e; }
                .btn-primary:hover { background-color: #28a686; border-color: #28a686; }
                .bg-primary-subtle { background-color: rgba(48, 195, 158, 0.1) !important; }
            `}</style>
        </div>
    );
};

const customStyles = {
    control: (base, state) => ({
        ...base,
        backgroundColor: '#f8f9fa',
        borderColor: state.isFocused ? '#30c39e' : 'transparent',
        borderRadius: '8px',
        padding: '2px',
        boxShadow: state.isFocused ? '0 0 0 3px rgba(48, 195, 158, 0.1)' : 'none',
        '&:hover': {
            borderColor: '#30c39e'
        }
    }),
    placeholder: (base) => ({
        ...base,
        fontSize: '13px',
        color: '#adb5bd'
    }),
    singleValue: (base) => ({
        ...base,
        fontSize: '14px',
        fontWeight: '500'
    })
};

export default B2BCreateTour;
