import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import Select from 'react-select';
import Slider from 'rc-slider';
import 'react-datepicker/dist/react-datepicker.css';
import 'rc-slider/assets/index.css';

const TourSearchForm = ({ onSearch }) => {
    // State for form fields
    const [departureCity, setDepartureCity] = useState(null);
    const [destination, setDestination] = useState(null);
    const [dateRange, setDateRange] = useState([null, null]);
    const [startDate, endDate] = dateRange;
    const [guests, setGuests] = useState({ adults: 2, children: 0, rooms: 1 });
    const [showGuestPopover, setShowGuestPopover] = useState(false);
    const [showAdvanced, setShowAdvanced] = useState(false);

    // Advanced Filters State
    const [priceRange, setPriceRange] = useState([100, 5000]);
    const [durationRange, setDurationRange] = useState([3, 14]);

    const departureOptions = [
        { value: "TAS", label: "ðŸ“ Tashkent" },
        { value: "SAM", label: "ðŸ“ Samarkand" },
        { value: "BUK", label: "ðŸ“ Bukhara" }
    ];

    const destinationOptions = [
        { value: "UAE", label: "ðŸ‡¦ðŸ‡ª UAE" },
        { value: "TR", label: "ðŸ‡¹ðŸ‡· Turkey" },
        { value: "MV", label: "ðŸ‡²ðŸ‡» Maldives" }
    ];

    const handleSubmit = (e) => {
        e.preventDefault();
        onSearch({
            departureCity,
            destination,
            dateRange,
            guests,
            priceRange,
            durationRange
        });
    };

    return (
        <div className="search-form-wrap shadow-sm rounded-4 bg-white p-3">
            <form onSubmit={handleSubmit} className="d-flex flex-wrap align-items-end gap-3 horizontal-search">
                {/* From */}
                <div className="flex-grow-1" style={{ minWidth: '200px' }}>
                    <label className="x-small text-muted fw-bold text-uppercase mb-1 px-2">Origin</label>
                    <Select
                        className="basic-single b2b-select"
                        classNamePrefix="select"
                        isClearable
                        isSearchable
                        options={departureOptions}
                        placeholder="Dallas, TX"
                        value={departureCity}
                        onChange={setDepartureCity}
                        styles={customStyles}
                    />
                </div>

                {/* Switch Icon */}
                <div className="align-self-center mt-3 pt-2">
                    <i className="fa fa-exchange-alt text-primary opacity-50"></i>
                </div>

                {/* To */}
                <div className="flex-grow-1" style={{ minWidth: '200px' }}>
                    <label className="x-small text-muted fw-bold text-uppercase mb-1 px-2">Destination</label>
                    <Select
                        className="basic-single b2b-select"
                        classNamePrefix="select"
                        isClearable
                        isSearchable
                        isMulti
                        options={destinationOptions}
                        placeholder="Where to?"
                        value={destination}
                        onChange={setDestination}
                        styles={customStyles}
                    />
                </div>

                {/* Dates */}
                <div style={{ minWidth: '220px' }}>
                    <label className="x-small text-muted fw-bold text-uppercase mb-1 px-2">Date Range</label>
                    <div className="b2b-field-input rounded-3 border px-3 py-2 d-flex align-items-center">
                        <DatePicker
                            selectsRange={true}
                            startDate={startDate}
                            endDate={endDate}
                            onChange={(update) => setDateRange(update)}
                            placeholderText="10/17/2026 - 10/24/2026"
                            className="bg-transparent border-0 w-100 small shadow-none outline-none"
                            autoComplete="off"
                        />
                        <i className="fa fa-calendar text-muted small ms-2"></i>
                    </div>
                </div>

                {/* Guests - Compact */}
                <div style={{ minWidth: '150px' }}>
                    <label className="x-small text-muted fw-bold text-uppercase mb-1 px-2">Equipment/Load</label>
                    <div
                        className="b2b-field-input rounded-3 border px-3 py-2 d-flex align-items-center cursor-pointer bg-light"
                        onClick={() => setShowGuestPopover(!showGuestPopover)}
                    >
                        <span className="small text-dark fw-bold">{guests.adults + guests.children} PAX, {guests.rooms}R</span>
                        <i className="fa fa-chevron-down ms-auto text-muted x-small"></i>
                    </div>
                    {showGuestPopover && (
                        <div className="position-absolute bg-white shadow-lg rounded-4 p-4 mt-2 border" style={{ zIndex: 100, minWidth: '300px' }}>
                            <div className="d-flex justify-content-between align-items-center mb-3">
                                <div className="fw-bold">Adults <div className="text-muted x-small fw-normal">Age 13+</div></div>
                                <div className="d-flex align-items-center border rounded-pill px-2">
                                    <button type="button" className="btn btn-sm btn-link text-dark py-0" onClick={() => setGuests({ ...guests, adults: Math.max(1, guests.adults - 1) })}>-</button>
                                    <span className="mx-3 fw-bold">{guests.adults}</span>
                                    <button type="button" className="btn btn-sm btn-link text-dark py-0" onClick={() => setGuests({ ...guests, adults: guests.adults + 1 })}>+</button>
                                </div>
                            </div>
                            <div className="d-flex justify-content-between align-items-center mb-3">
                                <div className="fw-bold">Children <div className="text-muted x-small fw-normal">Age 2-12</div></div>
                                <div className="d-flex align-items-center border rounded-pill px-2">
                                    <button type="button" className="btn btn-sm btn-link text-dark py-0" onClick={() => setGuests({ ...guests, children: Math.max(0, guests.children - 1) })}>-</button>
                                    <span className="mx-3 fw-bold">{guests.children}</span>
                                    <button type="button" className="btn btn-sm btn-link text-dark py-0" onClick={() => setGuests({ ...guests, children: guests.children + 1 })}>+</button>
                                </div>
                            </div>
                            <button type="button" className="btn btn-primary w-100 rounded-pill fw-bold btn-sm py-2 mt-2" onClick={() => setShowGuestPopover(false)}>Done</button>
                        </div>
                    )}
                </div>

                {/* Search Button */}
                <button type="submit" className="btn btn-primary rounded-pill px-4 py-2 fw-bold shadow-sm d-flex align-items-center h-100">
                    <i className="fa fa-search me-2"></i> SEARCH
                </button>

                {/* Advanced Toggle */}
                <button
                    type="button"
                    className="btn btn-light rounded-circle p-2 border shadow-sm align-self-center mt-3 pt-2"
                    onClick={() => setShowAdvanced(!showAdvanced)}
                >
                    <i className={`fa fa-sliders-h ${showAdvanced ? 'text-primary' : 'text-muted'}`}></i>
                </button>
            </form>

            {showAdvanced && (
                <div className="mt-4 pt-4 border-top animate__animated animate__fadeIn">
                    <div className="row g-4">
                        <div className="col-md-4">
                            <label className="x-small text-muted fw-bold text-uppercase mb-3 d-block">Price Range: ${priceRange[0]} - ${priceRange[1]}</label>
                            <Slider
                                range
                                min={0}
                                max={10000}
                                step={100}
                                value={priceRange}
                                onChange={setPriceRange}
                                trackStyle={{ backgroundColor: '#30c39e' }}
                                handleStyle={{ borderColor: '#30c39e', backgroundColor: '#30c39e' }}
                            />
                        </div>
                        <div className="col-md-4 border-start">
                            <label className="x-small text-muted fw-bold text-uppercase mb-3 d-block">Duration (Nights): {durationRange[0]} - {durationRange[1]}</label>
                            <Slider
                                range
                                min={1}
                                max={30}
                                value={durationRange}
                                onChange={setDurationRange}
                                trackStyle={{ backgroundColor: '#30c39e' }}
                                handleStyle={{ borderColor: '#30c39e', backgroundColor: '#30c39e' }}
                            />
                        </div>
                        <div className="col-md-4 border-start">
                            <label className="x-small text-muted fw-bold text-uppercase mb-3 d-block">Service Requirements</label>
                            <div className="d-flex gap-2 flex-wrap">
                                <span className="badge bg-light text-dark border p-2 px-3 rounded-pill cursor-pointer"><i className="fa fa-utensils me-2"></i>All Inclusive</span>
                                <span className="badge bg-light text-dark border p-2 px-3 rounded-pill cursor-pointer"><i className="fa fa-hotel me-2"></i>5 Star Hotel</span>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            <style>{`
                .b2b-field-input {
                    background-color: #f8f9fa;
                    border-color: #e9ecef !important;
                    transition: all 0.2s;
                }
                .b2b-field-input:focus-within {
                    border-color: #30c39e !important;
                    background-color: #fff;
                    box-shadow: 0 0 0 4px rgba(48, 195, 158, 0.1);
                }
                .x-small { font-size: 11px; }
                .outline-none { outline: none; }
                .react-datepicker-wrapper { width: 100%; }
            `}</style>
        </div>
    );
};

