    control: (base) => ({
        ...base,
        backgroundColor: '#f8f9fa',
        borderColor: '#e9ecef',
        borderRadius: '12px',
        padding: '2px 8px',
        boxShadow: 'none',
        '&:hover': {
            borderColor: '#30c39e'
        }
    }),
    placeholder: (base) => ({
        ...base,
        fontSize: '14px',
        color: '#6c757d'
    })
};

