import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { authAPI } from '../services/api';
import { useTranslation } from 'react-i18next';

const B2BRegister = () => {
    const { t } = useTranslation();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        companyName: '',
        licenseNumber: '',
        contactPerson: '',
        phone: '',
        email: '',
        password: '',
        confirmPassword: ''
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [success, setSuccess] = useState(false);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.id]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        if (formData.password !== formData.confirmPassword) {
            setError('Passwords do not match');
            return;
        }

        setLoading(true);
        try {
            await authAPI.register({
                ...formData,
                role: 'agent' // Specifically register as B2B agent
            });
            setSuccess(true);
            setTimeout(() => navigate('/login'), 3000);
        } catch (err) {
            setError(err.message || 'Registration failed');
        } finally {
            setLoading(false);
        }
    };

    if (success) {
        return (
            <div className="container-xxl py-5">
                <div className="container text-center">
                    <div className="alert alert-success d-inline-block p-5 rounded shadow-lg">
                        <i className="fa fa-check-circle fa-4x mb-3 text-success"></i>
                        <h2>Registration Successful!</h2>
                        <p className="mb-0">Your account has been created. Redirecting to login...</p>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="container-xxl py-5">
            <div className="container">
                <div className="text-center wow fadeInUp" data-wow-delay="0.1s">
                    <h6 className="section-title bg-white text-center text-primary px-3">B2B Partnership</h6>
                    <h1 className="mb-5">Register Your Business</h1>
                </div>
                <div className="row g-4 justify-content-center">
                    <div className="col-lg-6 wow fadeInUp" data-wow-delay="0.1s">
                        <div className="bg-light p-5 rounded shadow-sm">
                            <form onSubmit={handleSubmit}>
                                <div className="row g-3">
                                    <div className="col-md-6">
                                        <div className="form-floating">
                                            <input
                                                type="text"
                                                className="form-control"
                                                id="companyName"
                                                placeholder="Company Name"
                                                value={formData.companyName}
                                                onChange={handleChange}
                                                required
                                            />
                                            <label htmlFor="companyName">Company Name</label>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-floating">
                                            <input
                                                type="text"
                                                className="form-control"
                                                id="licenseNumber"
                                                placeholder="License/TIN"
                                                value={formData.licenseNumber}
                                                onChange={handleChange}
                                                required
                                            />
                                            <label htmlFor="licenseNumber">TIN / License Number</label>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-floating">
                                            <input
                                                type="text"
                                                className="form-control"
                                                id="contactPerson"
                                                placeholder="Contact Person"
                                                value={formData.contactPerson}
                                                onChange={handleChange}
                                                required
                                            />
                                            <label htmlFor="contactPerson">Contact Person Name</label>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-floating">
                                            <input
                                                type="tel"
                                                className="form-control"
                                                id="phone"
                                                placeholder="Phone"
                                                value={formData.phone}
                                                onChange={handleChange}
                                                required
                                            />
                                            <label htmlFor="phone">Contact Phone</label>
                                        </div>
                                    </div>
                                    <div className="col-12">
                                        <div className="form-floating">
                                            <input
                                                type="email"
                                                className="form-control"
                                                id="email"
                                                placeholder="Email"
                                                value={formData.email}
                                                onChange={handleChange}
                                                required
                                            />
                                            <label htmlFor="email">Work Email</label>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-floating">
                                            <input
                                                type="password"
                                                className="form-control"
                                                id="password"
                                                placeholder="Password"
                                                value={formData.password}
                                                onChange={handleChange}
                                                required
                                            />
                                            <label htmlFor="password">Password</label>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-floating">
                                            <input
                                                type="password"
                                                className="form-control"
                                                id="confirmPassword"
                                                placeholder="Confirm Password"
                                                value={formData.confirmPassword}
                                                onChange={handleChange}
                                                required
                                            />
                                            <label htmlFor="confirmPassword">Confirm Password</label>
                                        </div>
                                    </div>
                                    {error && (
                                        <div className="col-12">
                                            <div className="alert alert-danger mb-0" role="alert">
                                                {error}
                                            </div>
                                        </div>
                                    )}
                                    <div className="col-12">
                                        <button className="btn btn-primary w-100 py-3" type="submit" disabled={loading}>
                                            {loading ? 'Processing...' : 'Register as Partner'}
                                        </button>
                                    </div>
                                    <div className="col-12 text-center">
                                        <p className="mb-0">
                                            Already have a partner account? <Link to="/login" className="text-primary fw-bold">Login here</Link>
                                        </p>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default B2BRegister;
