import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './HotDeal.css';

const HotDeal = () => {
    const calculateTimeLeft = () => {
        const targetDate = new Date();
        // Set target to tomorrow midnight for demo purposes
        targetDate.setHours(24, 0, 0, 0);

        const difference = +targetDate - +new Date();
        let timeLeft = {};

        if (difference > 0) {
            timeLeft = {
                hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
                minutes: Math.floor((difference / 1000 / 60) % 60),
                seconds: Math.floor((difference / 1000) % 60),
            };
        }

        return timeLeft;
    };

    const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

    useEffect(() => {
        const timer = setTimeout(() => {
            setTimeLeft(calculateTimeLeft());
        }, 1000);

        return () => clearTimeout(timer);
    });

    return (
        <div className="container-xxl py-5 hot-deal-section">
            <div className="container">
                <div className="row g-5 align-items-center">
                    <div className="col-lg-6 wow fadeInUp" data-wow-delay="0.1s">
                        <div className="position-relative h-100 hot-deal-img-wrapper">
                            <img
                                className="img-fluid rounded w-100 h-100"
                                src="assets/img/package-3.jpg"
                                alt="Hot Deal"
                                style={{ objectFit: "cover", minHeight: '400px' }}
                            />
                            <div className="promo-badge">
                                <span>-40%</span>
                                <small>Chegirma</small>
                            </div>
                        </div>
                    </div>
                    <div className="col-lg-6 wow fadeInUp" data-wow-delay="0.3s">
                        <h6 className="section-title bg-white text-start text-danger pe-3">
                            QAYNOQ TAKLIF ðŸ”¥
                        </h6>
                        <h1 className="mb-4">Antaliya - Premium Sayohat</h1>
                        <p className="mb-4 text-justify">
                            Faqat bugun! Antaliyaning eng hashamatli mehmonxonalarida 5 yulduzli hordiq chiqarish imkoniyatini qo'ldan boy bermang. Narx ichiga barcha xizmatlar (All Inclusive) kiritilgan.
                        </p>

                        <div className="countdown-container mb-4">
                            <div className="countdown-item">
                                <span className="number">{timeLeft.hours || '00'}</span>
                                <span className="label">Soat</span>
                            </div>
                            <div className="countdown-item">
                                <span className="number">{timeLeft.minutes || '00'}</span>
                                <span className="label">Daqiqa</span>
                            </div>
                            <div className="countdown-item">
                                <span className="number">{timeLeft.seconds || '00'}</span>
                                <span className="label">Soniya</span>
                            </div>
                        </div>

                        <div className="price-tag mb-4">
                            <span className="old-price">$1200</span>
                            <span className="new-price text-primary">$720</span>
                            <small className="ms-2">/ kishi boshiga</small>
                        </div>

                        <Link to="/tour/1" className="btn btn-primary py-3 px-5 mt-2 rounded-pill shadow">
                            Hozir Band Qiling
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default HotDeal;
