import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function BandQilish() {
    const location = useLocation();
    const navigate = useNavigate();
    const selectedPackage = location.state || {
        id: 0,
        title: "Maxsus Paket",
        location: "Noma'lum",
        duration: "Kelishiladi",
        price: 0,
        image: "/assets/img/booking.jpg"
    };

    const [step, setStep] = useState(1);
    const [formData, setFormData] = useState({
        travelDate: '',
        adults: 1,
        children: 0,
        rooms: 1,
        addons: {
            transfer: false,
            hotelUpgrade: false,
            guide: false,
            meals: false,
            insurance: false
        },
        travelers: [
            { firstName: '', lastName: '', dob: '', gender: 'Male', nationality: '', passport: '' }
        ],
        contact: {
            phone: '',
            email: '',
            city: '',
            notes: ''
        },
        paymentMethod: 'Card'
    });

    const [totalPrice, setTotalPrice] = useState(selectedPackage.price);

    useEffect(() => {
        let price = selectedPackage.price * formData.adults;
        price += (selectedPackage.price * 0.5) * formData.children;
        if (formData.addons.transfer) price += 50;
        if (formData.addons.hotelUpgrade) price += 100;
        if (formData.addons.guide) price += 80;
        if (formData.addons.meals) price += 60;
        if (formData.addons.insurance) price += 30;
        setTotalPrice(price);

        const totalPeople = formData.adults + formData.children;
        if (formData.travelers.length !== totalPeople) {
            const newTravelers = [...formData.travelers];
            if (newTravelers.length < totalPeople) {
                for (let i = newTravelers.length; i < totalPeople; i++) {
                    newTravelers.push({ firstName: '', lastName: '', dob: '', gender: 'Male', nationality: '', passport: '' });
                }
            } else {
                newTravelers.splice(totalPeople);
            }
            setFormData(prev => ({ ...prev, travelers: newTravelers }));
        }
    }, [formData.adults, formData.children, formData.addons, selectedPackage.price]);

    const nextStep = () => setStep(step + 1);
    const prevStep = () => setStep(step - 1);

    const handleInputChange = (stepKey, field, value) => {
        if (stepKey === 'direct') {
            setFormData(prev => ({ ...prev, [field]: value }));
        } else {
            setFormData(prev => ({
                ...prev,
                [stepKey]: { ...prev[stepKey], [field]: value }
            }));
        }
    };

    const handleTravelerChange = (index, field, value) => {
        const updatedTravelers = formData.travelers.map((t, i) =>
            i === index ? { ...t, [field]: value } : t
        );
        setFormData(prev => ({ ...prev, travelers: updatedTravelers }));
    };

    const handleAddonChange = (addon) => {
        setFormData(prev => ({
            ...prev,
            addons: { ...prev.addons, [addon]: !prev.addons[addon] }
        }));
    };

    const steps = [
        "Sayohat tafsilotlari",
        "Qo'shimcha xizmatlar",
        "Sayohatchilar ma'lumotlari",
        "Bog'lanish ma'lumotlari",
        "Buyurtmani tekshirish",
        "To'lov usuli",
        "Tasdiqlash"
    ];

    return (
        <div className="container-fluid py-5 bg-light" style={{ minHeight: '100vh' }}>
            <div className="container">
                <div className="d-flex align-items-center mb-4">
                    <div className="bg-primary text-white rounded p-2 me-3">
                        <i className="fa fa-plus-square"></i>
                    </div>
                    <h2 className="m-0">Buyurtma yaratish - {selectedPackage.title}</h2>
                </div>

                <div className="row g-4 justify-content-center">
                    {/* Left Side: Vertical Stepper */}
                    <div className="col-lg-1 d-none d-lg-block text-center position-relative">
                        <div className="d-flex flex-column align-items-center h-100 py-3">
                            {steps.map((_, s) => {
                                const stepNum = s + 1;
                                return (
                                    <React.Fragment key={stepNum}>
                                        <div
                                            className={`rounded-circle d-flex align-items-center justify-content-center mb-1 ${step >= stepNum ? 'bg-primary text-white shadow' : 'bg-white border text-muted'}`}
                                            style={{ width: '40px', height: '40px', zIndex: 2, fontSize: '1.1rem', fontWeight: 'bold' }}
                                        >
                                            {stepNum}
                                        </div>
                                        {stepNum < steps.length && (
                                            <div
                                                className={`flex-grow-1 my-1 ${step > stepNum ? 'bg-primary' : 'bg-secondary-subtle border-start border-2 border-dotted'}`}
                                                style={{ width: '2px', minHeight: '40px', zIndex: 1 }}
                                            ></div>
                                        )}
                                    </React.Fragment>
                                );
                            })}
                        </div>
                    </div>

                    {/* Middle: Main Form Card */}
                    <div className="col-lg-9">
                        <div className="card border-0 shadow-sm p-4 h-100">
                            <div className="d-flex justify-content-between align-items-center mb-4">
                                <h4 className="m-0 text-primary">{steps[step - 1]}</h4>
                                {step < 7 && (
                                    <div className="badge bg-primary fs-6">
                                        Jami: ${totalPrice}
                                    </div>
                                )}
                            </div>

                            {/* Step Content */}
                            <div className="step-content">
                                {step === 1 && (
                                    <div className="row g-3">
                                        <div className="col-md-6 mb-3">
                                            <label className="form-label fw-bold small text-uppercase text-muted">Sayohat sanasi</label>
                                            <input type="date" className="form-control form-control-lg border-light bg-light" value={formData.travelDate} onChange={(e) => handleInputChange('direct', 'travelDate', e.target.value)} />
                                        </div>
                                        <div className="col-md-6 mb-3">
                                            <label className="form-label fw-bold small text-uppercase text-muted">Kattalar soni</label>
                                            <input type="number" min="1" className="form-control form-control-lg border-light bg-light" value={formData.adults} onChange={(e) => handleInputChange('direct', 'adults', parseInt(e.target.value))} />
                                        </div>
                                        <div className="col-md-6 mb-3">
                                            <label className="form-label fw-bold small text-uppercase text-muted">Bolalar soni</label>
                                            <input type="number" min="0" className="form-control form-control-lg border-light bg-light" value={formData.children} onChange={(e) => handleInputChange('direct', 'children', parseInt(e.target.value))} />
                                        </div>
                                        <div className="col-md-6 mb-3">
                                            <label className="form-label fw-bold small text-uppercase text-muted">Xonalar soni</label>
                                            <input type="number" min="1" className="form-control form-control-lg border-light bg-light" value={formData.rooms} onChange={(e) => handleInputChange('direct', 'rooms', parseInt(e.target.value))} />
                                        </div>
                                    </div>
                                )}

                                {step === 2 && (
                                    <div className="row g-3">
                                        {[
                                            { id: 'transfer', label: 'Aeroport transferi ($50)', key: 'transfer' },
                                            { id: 'upgrade', label: 'Mehmonxonani yangilash ($100)', key: 'hotelUpgrade' },
                                            { id: 'guide', label: 'Gid xizmati ($80)', key: 'guide' },
                                            { id: 'meals', label: 'Ovqatlanish (Full board) ($60)', key: 'meals' },
                                            { id: 'insurance', label: "Sayohat sug'urtasi ($30)", key: 'insurance' }
                                        ].map((addon) => (
                                            <div className="col-12 mb-2" key={addon.id}>
                                                <div className="form-check p-3 border rounded border-light bg-light">
                                                    <input className="form-check-input ms-0" type="checkbox" id={addon.id} checked={formData.addons[addon.key]} onChange={() => handleAddonChange(addon.key)} />
                                                    <label className="form-check-label ms-3 fw-bold" htmlFor={addon.id}>{addon.label}</label>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}

                                {step === 3 && (
                                    <div style={{ maxHeight: '500px', overflowY: 'auto' }} className="pe-2">
                                        {formData.travelers.map((traveler, index) => (
                                            <div key={index} className="mb-4 p-4 border-0 rounded bg-light shadow-sm">
                                                <h5 className="text-primary mb-3"><i className="fa fa-user me-2"></i> {index + 1}-sayohatchi</h5>
                                                <div className="row g-3">
                                                    <div className="col-md-6">
                                                        <input type="text" className="form-control border-white shadow-sm" placeholder="Ism" value={traveler.firstName} onChange={(e) => handleTravelerChange(index, 'firstName', e.target.value)} />
                                                    </div>
                                                    <div className="col-md-6">
                                                        <input type="text" className="form-control border-white shadow-sm" placeholder="Familiya" value={traveler.lastName} onChange={(e) => handleTravelerChange(index, 'lastName', e.target.value)} />
                                                    </div>
                                                    <div className="col-md-6">
                                                        <input type="date" className="form-control border-white shadow-sm" value={traveler.dob} onChange={(e) => handleTravelerChange(index, 'dob', e.target.value)} />
                                                    </div>
                                                    <div className="col-md-6">
                                                        <select className="form-select border-white shadow-sm" value={traveler.gender} onChange={(e) => handleTravelerChange(index, 'gender', e.target.value)}>
                                                            <option value="Male">Erkak</option>
                                                            <option value="Female">Ayol</option>
                                                        </select>
                                                    </div>
                                                    <div className="col-12">
                                                        <input type="text" className="form-control border-white shadow-sm" placeholder="Pasport raqami" value={traveler.passport} onChange={(e) => handleTravelerChange(index, 'passport', e.target.value)} />
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}

                                {step === 4 && (
                                    <div className="row g-3">
                                        <div className="col-md-6 mb-3">
                                            <input type="tel" className="form-control form-control-lg border-light bg-light" placeholder="Telefon raqami" value={formData.contact.phone} onChange={(e) => handleInputChange('contact', 'phone', e.target.value)} />
                                        </div>
                                        <div className="col-md-6 mb-3">
                                            <input type="email" className="form-control form-control-lg border-light bg-light" placeholder="Email manzili" value={formData.contact.email} onChange={(e) => handleInputChange('contact', 'email', e.target.value)} />
                                        </div>
                                        <div className="col-12 mb-3">
                                            <input type="text" className="form-control form-control-lg border-light bg-light" placeholder="Shahar / Davlat" value={formData.contact.city} onChange={(e) => handleInputChange('contact', 'city', e.target.value)} />
                                        </div>
                                        <div className="col-12">
                                            <textarea className="form-control border-light bg-light" rows="4" placeholder="Maxsus so'rovlar (ixtiyoriy)" value={formData.contact.notes} onChange={(e) => handleInputChange('contact', 'notes', e.target.value)}></textarea>
                                        </div>
                                    </div>
                                )}

                                {step === 5 && (
                                    <div className="bg-light p-4 rounded text-dark shadow-sm">
                                        <div className="row">
                                            <div className="col-md-6 border-end">
                                                <p className="mb-1 text-muted small text-uppercase">Paket</p>
                                                <p className="fw-bold">{selectedPackage.title}</p>
                                                <p className="mb-1 text-muted small text-uppercase">Sana</p>
                                                <p className="fw-bold">{formData.travelDate || 'Tanlanmagan'}</p>
                                            </div>
                                            <div className="col-md-6 ps-md-4">
                                                <p className="mb-1 text-muted small text-uppercase">Xonalar & Odamlar</p>
                                                <p className="fw-bold">{formData.adults}ta katta, {formData.children}ta bola / {formData.rooms}ta xona</p>
                                                <p className="mb-1 text-muted small text-uppercase">Qo'shimchalar</p>
                                                <p className="fw-bold text-truncate">{Object.entries(formData.addons).filter(([_, v]) => v).map(([k]) => k).join(', ') || "Yo'q"}</p>
                                            </div>
                                        </div>
                                    </div>
                                )}

                                {step === 6 && (
                                    <div className="row g-3">
                                        <div className="col-12 mb-4 text-center">
                                            <div className="p-4 bg-light rounded border border-primary-subtle">
                                                <h5>Xavfsiz to'lov</h5>
                                                <p className="text-muted">Iltimos, o'zingizga qulay to'lov usulini tanlang.</p>
                                            </div>
                                        </div>
                                        <div className="col-12">
                                            <select className="form-select form-select-lg border-light bg-light" value={formData.paymentMethod} onChange={(e) => handleInputChange('direct', 'paymentMethod', e.target.value)}>
                                                <option value="Card"> Kredit / Debet karta</option>
                                                <option value="Bank"> Bank o'tkazmasi</option>
                                                <option value="Online"> Onlayn to'lov (Payme/Click)</option>
                                            </select>
                                        </div>
                                        <div className="col-12 mt-4 shadow-none">
                                            <div className="form-check p-3 border rounded border-light bg-light">
                                                <input className="form-check-input ms-0" type="checkbox" id="terms" required />
                                                <label className="form-check-label ms-3 fw-bold" htmlFor="terms">Foydalanish shartlari va qoidalariga roziman</label>
                                            </div>
                                        </div>
                                    </div>
                                )}

                                {step === 7 && (
                                    <div className="text-center py-5">
                                        <div className="mb-4">
                                            <div className="bg-success text-white d-inline-block rounded-circle p-4 shadow">
                                                <i className="fa fa-check fa-4x"></i>
                                            </div>
                                        </div>
                                        <h2 className="mb-3">Tabriklaymiz!</h2>
                                        <h5 className="text-muted mb-4">Sizning sayohatingiz muvaffaqiyatli band qilindi.</h5>
                                        <div className="bg-light p-3 rounded mb-4 d-inline-block px-5">
                                            <span className="text-muted small d-block">BUYURTMA RAQAMI</span>
                                            <span className="fw-bold h4">#AT-{Math.floor(Math.random() * 1000000)}</span>
                                        </div>
                                        <p className="text-center mb-5">Barcha ma'lumotlar emailingizga yuborildi. Tez orada menejerimiz siz bilan bog'lanadi.</p>
                                        <button className="btn btn-primary btn-lg rounded-pill px-5" onClick={() => navigate('/packages')}>Bosh sahifaga qaytish</button>
                                    </div>
                                )}
                            </div>

                            {/* Nav Buttons */}
                            {step < 7 && (
                                <div className="d-flex justify-content-between mt-auto pt-5">
                                    {step > 1 ? (
                                        <button className="btn btn-outline-secondary border-0 fw-bold" onClick={prevStep}>
                                            <i className="fa fa-arrow-left me-2"></i> Orqaga
                                        </button>
                                    ) : <div></div>}
                                    <button className="btn btn-primary btn-lg px-5 rounded shadow" onClick={nextStep}>
                                        {step === 6 ? "To'lovni tasdiqlash" : 'Davom etish'} <i className="fa fa-arrow-right ms-2"></i>
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default BandQilish;
