import React, { useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

const TourDetails = () => {
    const { id } = useParams();

    // Scroll to top on mount
    useEffect(() => {
        window.scrollTo(0, 0);
    }, []);

    // Mock data for the demonstration
    const tourData = {
        title: "Antaliya - Premium Sayohat",
        price: 720,
        duration: "7 kun / 6 tun",
        groupSize: "15+ kishi",
        location: "Turkiya, Antaliya",
        description: "Turkiyaning eng mashhur va quyoshli kurortlaridan biri bo'lgan Antaliyada unutilmas hordiq chiqarish imkoniyatini qo'ldan boy bermang. Ushbu paket barcha qulayliklarni o'z ichiga olgan bo'lib, sizga haqiqiy dam olish zavqini bag'ishlaydi.",
        highlights: [
            "5* All-Inclusive mehmonxona",
            "To'g'ridan-to'g'ri aviareys",
            "Professional gid xizmati",
            "Ekskursiya dasturlari"
        ],
        itinerary: [
            { day: "1-kun", title: "Yetib kelish va joylashish", desc: "Aeroportda kutib olish va 5 yulduzli mehmonxonaga transfer. Bo'sh vaqt va kechki ovqat." },
            { day: "2-kun", title: "Shahar bo'ylab sayohat", desc: "Antaliyaning tarixiy markazi - Kaleichi va Düden sharsharasiga sayohat." },
            { day: "3-kun", title: "O'rta yer dengizi kruizi", desc: "Yaxtada sayohat va dengizda cho'milish." },
            { day: "4-kun", title: "Sopol va bozorlar", desc: "Mahalliy bozorlar va hunarmandchilik markazlariga tashrif." },
            { day: "5-kun", title: "Erkin kun", desc: "Sohilda dam olish yoki qo'shimcha ekskursiyalar." },
            { day: "6-kun", title: "Kechki shou", desc: "Turk kechasi va milliy taomlar festivali." },
            { day: "7-kun", title: "Xayrlashuv", desc: "Xaridorlar uchun bo'sh vaqt va aeroportga transfer." }
        ]
    };

    return (
        <div>
            {/* Hero Header */}
            <div className="container-fluid bg-primary py-5 mb-5 hero-header">
                <div className="container py-5">
                    <div className="row justify-content-center py-5">
                        <div className="col-lg-10 pt-lg-5 mt-lg-5 text-center">
                            <h1 className="display-3 text-white animated slideInDown">
                                {tourData.title}
                            </h1>
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb justify-content-center">
                                    <li className="breadcrumb-item"><Link to="/">Bosh sahifa</Link></li>
                                    <li className="breadcrumb-item"><Link to="/packages">Paketlar</Link></li>
                                    <li className="breadcrumb-item text-white active" aria-current="page">Batafsil</li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>
            </div>

            <div className="container-xxl py-5">
                <div className="container">
                    <div className="row g-5">
                        {/* Left Column: Details */}
                        <div className="col-lg-8">
                            <div className="mb-5">
                                <img className="img-fluid rounded mb-4 w-100" src="/assets/img/package-1.jpg" alt="" style={{ maxHeight: '500px', objectFit: 'cover' }} />
                                <h2 className="mb-4">Sayohat haqida</h2>
                                <p className="text-muted fs-5">{tourData.description}</p>
                            </div>

                            <div className="mb-5">
                                <h3 className="mb-4">Asosiy qulayliklar</h3>
                                <div className="row g-3">
                                    {tourData.highlights.map((h, index) => (
                                        <div className="col-sm-6" key={index}>
                                            <div className="d-flex align-items-center bg-light border-start border-5 border-primary p-3">
                                                <i className="fa fa-check text-primary me-3"></i>
                                                <span className="fw-bold">{h}</span>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>

                            <div className="mb-5">
                                <h3 className="mb-4">Sayohat dasturi (Itinerary)</h3>
                                <div className="itinerary-timeline ms-3 mt-4">
                                    {tourData.itinerary.map((item, index) => (
                                        <div className="itinerary-item mb-4 position-relative ps-4" key={index}>
                                            <div className="timeline-dot bg-primary position-absolute start-0 top-0 mt-1" style={{ width: 15, height: 15, borderRadius: '50%', marginLeft: -7 }}></div>
                                            <h5 className="text-primary">{item.day}: {item.title}</h5>
                                            <p className="mb-0 text-muted">{item.desc}</p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>

                        {/* Right Column: Sidebar */}
                        <div className="col-lg-4">
                            <div className="bg-light p-4 rounded mb-5 sticky-top" style={{ top: '100px' }}>
                                <h4 className="mb-4 border-bottom pb-3">Narxi: <span className="text-primary">${tourData.price}</span></h4>
                                <div className="d-flex justify-content-between mb-3 border-bottom pb-2">
                                    <span><i className="fa fa-calendar-alt text-primary me-2"></i>Davomiyligi:</span>
                                    <span className="fw-bold">{tourData.duration}</span>
                                </div>
                                <div className="d-flex justify-content-between mb-3 border-bottom pb-2">
                                    <span><i className="fa fa-user-friends text-primary me-2"></i>Guruh:</span>
                                    <span className="fw-bold">{tourData.groupSize}</span>
                                </div>
                                <div className="d-flex justify-content-between mb-4 border-bottom pb-2">
                                    <span><i className="fa fa-map-marker-alt text-primary me-2"></i>Manzil:</span>
                                    <span className="fw-bold">{tourData.location}</span>
                                </div>
                                <Link to="/booking" className="btn btn-primary w-100 py-3 rounded-pill shadow">Hozir band qilish</Link>

                                <div className="mt-4 pt-3 border-top">
                                    <p className="small text-center text-muted mb-0">Savollaringiz bormi? Biz bilan bog'laning:</p>
                                    <h5 className="text-center mt-2">+998 77 737 80 20</h5>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Inline Styles for Timeline */}
            <style>{`
        .itinerary-timeline {
            border-left: 2px solid #86b817;
        }
        .itinerary-item::before {
            content: '';
            position: absolute;
            left: 0;
            top: 25px;
            bottom: -25px;
            width: 0;
            border-left: 2px solid transparent;
        }
      `}</style>
        </div>
    );
};

export default TourDetails;
