import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function BandQilish() {
    const location = useLocation();
    const navigate = useNavigate();
    const selectedPackage = location.state || {
        id: 0,
        title: "Maxsus Paket",
        location: "Noma'lum",
        duration: "Kelishiladi",
        price: 0,
        image: "/assets/img/booking.jpg"
    };

    const [step, setStep] = useState(1);
    const [formData, setFormData] = useState({
        // Step 1: Travel Details
        travelDate: '',
        adults: 1,
        children: 0,
        rooms: 1,
        // Step 2: Additional Services
        addons: {
            transfer: false,
            hotelUpgrade: false,
            guide: false,
            meals: false,
            insurance: false
        },
        // Step 3: Traveler Information
        travelers: [
            { firstName: '', lastName: '', dob: '', gender: 'Male', nationality: '', passport: '' }
        ],
        // Step 4: Contact Information
        contact: {
            phone: '',
            email: '',
            city: '',
            notes: ''
        },
        // Step 6: Payment
        paymentMethod: 'Card'
    });

    const [totalPrice, setTotalPrice] = useState(selectedPackage.price);

    useEffect(() => {
        // Basic price calculation
        let price = selectedPackage.price * formData.adults;
        price += (selectedPackage.price * 0.5) * formData.children; // Children 50% discount

        if (formData.addons.transfer) price += 50;
        if (formData.addons.hotelUpgrade) price += 100;
        if (formData.addons.guide) price += 80;
        if (formData.addons.meals) price += 60;
        if (formData.addons.insurance) price += 30;

        setTotalPrice(price);

        // Update travelers array length based on count
        const totalPeople = formData.adults + formData.children;
        if (formData.travelers.length !== totalPeople) {
            const newTravelers = [...formData.travelers];
            if (newTravelers.length < totalPeople) {
                for (let i = newTravelers.length; i < totalPeople; i++) {
                    newTravelers.push({ firstName: '', lastName: '', dob: '', gender: 'Male', nationality: '', passport: '' });
                }
            } else {
                newTravelers.splice(totalPeople);
            }
            setFormData(prev => ({ ...prev, travelers: newTravelers }));
        }
    }, [formData.adults, formData.children, formData.addons, selectedPackage.price]);

    const nextStep = () => setStep(step + 1);
    const prevStep = () => setStep(step - 1);

    const handleInputChange = (stepKey, field, value) => {
        if (stepKey === 'direct') {
            setFormData(prev => ({ ...prev, [field]: value }));
        } else {
            setFormData(prev => ({
                ...prev,
                [stepKey]: { ...prev[stepKey], [field]: value }
            }));
        }
    };

    const handleTravelerChange = (index, field, value) => {
        const updatedTravelers = formData.travelers.map((t, i) =>
            i === index ? { ...t, [field]: value } : t
        );
        setFormData(prev => ({ ...prev, travelers: updatedTravelers }));
    };

    const handleAddonChange = (addon) => {
        setFormData(prev => ({
            ...prev,
            addons: { ...prev.addons, [addon]: !prev.addons[addon] }
        }));
    };

    return (
        <div className="container-xxl py-5">
            <div className="container">
                <div className="text-center wow fadeInUp" data-wow-delay="0.1s">
                    <h6 className="section-title bg-white text-center text-primary px-3">Band Qilish</h6>
                    <h1 className="mb-5">{selectedPackage.title} - {step}/7-qadam</h1>
                </div>

                {/* Stepper */}
                <div className="row justify-content-center mb-5">
                    <div className="col-lg-8">
                        <div className="d-flex justify-content-between position-relative">
                            {[1, 2, 3, 4, 5, 6, 7].map((s) => (
                                <div key={s} className="text-center" style={{ zIndex: 1 }}>
                                    <div className={`rounded-circle d-flex align-items-center justify-content-center ${step >= s ? 'bg-primary text-white' : 'bg-light'}`}
                                        style={{ width: '40px', height: '40px', margin: '0 auto' }}>
                                        {s}
                                    </div>
                                    <small className="d-none d-md-block">Qadam {s}</small>
                                </div>
                            ))}
                            <div className="position-absolute top-50 start-0 translate-middle-y w-100 bg-light" style={{ height: '2px', zIndex: 0 }}>
                                <div className="bg-primary h-100 transition" style={{ width: `${((step - 1) / 6) * 100}%` }}></div>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="row g-5 justify-content-center">
                    <div className="col-lg-8">
                        <div className="booking p-5 rounded shadow">
                            {/* Step 1: Travel Details */}
                            {step === 1 && (
                                <div className="wow fadeIn">
                                    <h3 className="text-white mb-4">Sayohat tafsilotlari</h3>
                                    <div className="row g-3">
                                        <div className="col-md-6">
                                            <label className="text-white mb-2">Sayohat sanasi</label>
                                            <input type="date" className="form-control" value={formData.travelDate} onChange={(e) => handleInputChange('direct', 'travelDate', e.target.value)} />
                                        </div>
                                        <div className="col-md-6">
                                            <label className="text-white mb-2">Kattalar soni</label>
                                            <input type="number" min="1" className="form-control" value={formData.adults} onChange={(e) => handleInputChange('direct', 'adults', parseInt(e.target.value))} />
                                        </div>
                                        <div className="col-md-6">
                                            <label className="text-white mb-2">Bolalar soni</label>
                                            <input type="number" min="0" className="form-control" value={formData.children} onChange={(e) => handleInputChange('direct', 'children', parseInt(e.target.value))} />
                                        </div>
                                        <div className="col-md-6">
                                            <label className="text-white mb-2">Xonalar soni</label>
                                            <input type="number" min="1" className="form-control" value={formData.rooms} onChange={(e) => handleInputChange('direct', 'rooms', parseInt(e.target.value))} />
                                        </div>
                                        <div className="col-12 mt-4">
                                            <h4 className="text-white">Umumiy narx: ${totalPrice}</h4>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {/* Step 2: Additional Services */}
                            {step === 2 && (
                                <div className="wow fadeIn">
                                    <h3 className="text-white mb-4">Qo'shimcha xizmatlar</h3>
                                    <div className="row g-3">
                                        {[
                                            { id: 'transfer', label: 'Aeroport transferi ($50)', key: 'transfer' },
                                            { id: 'upgrade', label: 'Mehmonxonani yangilash ($100)', key: 'hotelUpgrade' },
                                            { id: 'guide', label: 'Gid xizmati ($80)', key: 'guide' },
                                            { id: 'meals', label: 'Ovqatlanish (Full board) ($60)', key: 'meals' },
                                            { id: 'insurance', label: "Sayohat sug'urtasi ($30)", key: 'insurance' }
                                        ].map((addon) => (
                                            <div className="col-12" key={addon.id}>
                                                <div className="form-check">
                                                    <input className="form-check-input" type="checkbox" id={addon.id} checked={formData.addons[addon.key]} onChange={() => handleAddonChange(addon.key)} />
                                                    <label className="form-check-label text-white" htmlFor={addon.id}>{addon.label}</label>
                                                </div>
                                            </div>
                                        ))}
                                        <div className="col-12 mt-4">
                                            <h4 className="text-white">Umumiy narx: ${totalPrice}</h4>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {/* Step 3: Traveler Information */}
                            {step === 3 && (
                                <div className="wow fadeIn">
                                    <h3 className="text-white mb-4">Sayohatchilar ma'lumotlari</h3>
                                    <div style={{ maxHeight: '400px', overflowY: 'auto', paddingRight: '10px' }}>
                                        {formData.travelers.map((traveler, index) => (
                                            <div key={index} className="mb-4 p-3 border rounded bg-secondary-subtle">
                                                <h5 className="text-primary mb-3">{index + 1}-sayohatchi</h5>
                                                <div className="row g-3">
                                                    <div className="col-md-6">
                                                        <input type="text" className="form-control" placeholder="Ism" value={traveler.firstName} onChange={(e) => handleTravelerChange(index, 'firstName', e.target.value)} />
                                                    </div>
                                                    <div className="col-md-6">
                                                        <input type="text" className="form-control" placeholder="Familiya" value={traveler.lastName} onChange={(e) => handleTravelerChange(index, 'lastName', e.target.value)} />
                                                    </div>
                                                    <div className="col-md-6">
                                                        <input type="date" className="form-control" placeholder="Tug'ilgan sana" value={traveler.dob} onChange={(e) => handleTravelerChange(index, 'dob', e.target.value)} />
                                                    </div>
                                                    <div className="col-md-6">
                                                        <select className="form-select" value={traveler.gender} onChange={(e) => handleTravelerChange(index, 'gender', e.target.value)}>
                                                            <option value="Male">Erkak</option>
                                                            <option value="Female">Ayol</option>
                                                        </select>
                                                    </div>
                                                    <div className="col-12">
                                                        <input type="text" className="form-control" placeholder="Pasport raqami" value={traveler.passport} onChange={(e) => handleTravelerChange(index, 'passport', e.target.value)} />
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            )}

                            {/* Step 4: Contact Information */}
                            {step === 4 && (
                                <div className="wow fadeIn">
                                    <h3 className="text-white mb-4">Bog'lanish ma'lumotlari</h3>
                                    <div className="row g-3">
                                        <div className="col-md-6">
                                            <input type="tel" className="form-control" placeholder="Telefon raqami" value={formData.contact.phone} onChange={(e) => handleInputChange('contact', 'phone', e.target.value)} />
                                        </div>
                                        <div className="col-md-6">
                                            <input type="email" className="form-control" placeholder="Email manzili" value={formData.contact.email} onChange={(e) => handleInputChange('contact', 'email', e.target.value)} />
                                        </div>
                                        <div className="col-12">
                                            <input type="text" className="form-control" placeholder="Shahar / Davlat" value={formData.contact.city} onChange={(e) => handleInputChange('contact', 'city', e.target.value)} />
                                        </div>
                                        <div className="col-12">
                                            <textarea className="form-control" rows="3" placeholder="Maxsus so'rovlar (ixtiyoriy)" value={formData.contact.notes} onChange={(e) => handleInputChange('contact', 'notes', e.target.value)}></textarea>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {/* Step 5: Review Booking */}
                            {step === 5 && (
                                <div className="wow fadeIn">
                                    <h3 className="text-white mb-4">Buyurtmani tekshirish</h3>
                                    <div className="bg-light p-4 rounded text-dark">
                                        <p><strong>Paket:</strong> {selectedPackage.title}</p>
                                        <p><strong>Sana:</strong> {formData.travelDate}</p>
                                        <p><strong>Sayohatchilar:</strong> {formData.adults}ta katta, {formData.children}ta bola</p>
                                        <p><strong>Xonalar:</strong> {formData.rooms}ta</p>
                                        <p><strong>Qo'shimchalar:</strong> {Object.entries(formData.addons).filter(([_, v]) => v).map(([k]) => k).join(', ') || "Yo'q"}</p>
                                        <hr />
                                        <h4 className="text-primary">Jami: ${totalPrice}</h4>
                                    </div>
                                </div>
                            )}

                            {/* Step 6: Payment */}
                            {step === 6 && (
                                <div className="wow fadeIn">
                                    <h3 className="text-white mb-4">To'lov usulini tanlang</h3>
                                    <div className="row g-3">
                                        <div className="col-12">
                                            <select className="form-select" value={formData.paymentMethod} onChange={(e) => handleInputChange('direct', 'paymentMethod', e.target.value)}>
                                                <option value="Card">Kredit / Debet karta</option>
                                                <option value="Bank">Bank o'tkazmasi</option>
                                                <option value="Online">Onlayn to'lov (Payme/Click)</option>
                                            </select>
                                        </div>
                                        <div className="col-12 mt-4">
                                            <div className="form-check">
                                                <input className="form-check-input" type="checkbox" id="terms" required />
                                                <label className="form-check-label text-white" htmlFor="terms">Foydalanish shartlariga roziman</label>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {/* Step 7: Confirmation */}
                            {step === 7 && (
                                <div className="wow fadeIn text-center">
                                    <div className="mb-4">
                                        <i className="fa fa-check-circle fa-5x text-success"></i>
                                    </div>
                                    <h3 className="text-white mb-4">Muvaffaqiyatli band qilindi!</h3>
                                    <p className="text-white">Sizning band qilish raqamingiz: <strong>#AT-{Math.floor(Math.random() * 1000000)}</strong></p>
                                    <p className="text-white mb-4">Tasdiqlash xati va hisob-faktura emailingizga yuborildi.</p>
                                    <button className="btn btn-primary py-3 px-5" onClick={() => navigate('/packages')}>Paketlar sahifasiga qaytish</button>
                                </div>
                            )}

                            {/* Navigation Buttons */}
                            {step < 7 && (
                                <div className="d-flex justify-content-between mt-5">
                                    {step > 1 ? (
                                        <button className="btn btn-outline-light py-3 px-5" onClick={prevStep}>Orqaga</button>
                                    ) : <div></div>}
                                    <button className="btn btn-primary py-3 px-5" onClick={nextStep}>
                                        {step === 6 ? "To'lovni amalga oshirish" : 'Keyingi qadam'}
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default BandQilish;
