import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function BandQilish() {
    const location = useLocation();
    const navigate = useNavigate();
    const selectedPackage = location.state || {
        id: 0,
        title: "Maxsus Paket",
        location: "Noma'lum",
        duration: "Kelishiladi",
        price: 0,
        image: "/assets/img/booking.jpg"
    };

    const [isConfirmed, setIsConfirmed] = useState(false);
    const [bookingId, setBookingId] = useState('');
    const [formData, setFormData] = useState({
        travelDate: '',
        adults: 1,
        children: 0,
        rooms: 1,
        addons: {
            transfer: false,
            hotelUpgrade: false,
            guide: false,
            meals: false,
            insurance: false
        },
        travelers: [
            { firstName: '', lastName: '', dob: '', gender: 'Male', nationality: '', passport: '' }
        ],
        contact: {
            phone: '',
            email: '',
            city: '',
            notes: ''
        },
        paymentMethod: 'Card'
    });

    const [totalPrice, setTotalPrice] = useState(selectedPackage.price);

    useEffect(() => {
        let price = selectedPackage.price * formData.adults;
        price += (selectedPackage.price * 0.5) * formData.children;
        if (formData.addons.transfer) price += 50;
        if (formData.addons.hotelUpgrade) price += 100;
        if (formData.addons.guide) price += 80;
        if (formData.addons.meals) price += 60;
        if (formData.addons.insurance) price += 30;
        setTotalPrice(price);

        const totalPeople = formData.adults + formData.children;
        if (formData.travelers.length !== totalPeople) {
            const newTravelers = [...formData.travelers];
            if (newTravelers.length < totalPeople) {
                for (let i = newTravelers.length; i < totalPeople; i++) {
                    newTravelers.push({ firstName: '', lastName: '', dob: '', gender: 'Male', nationality: '', passport: '' });
                }
            } else {
                newTravelers.splice(totalPeople);
            }
            setFormData(prev => ({ ...prev, travelers: newTravelers }));
        }
    }, [formData.adults, formData.children, formData.addons, selectedPackage.price]);

    const handleInputChange = (stepKey, field, value) => {
        if (stepKey === 'direct') {
            setFormData(prev => ({ ...prev, [field]: value }));
        } else {
            setFormData(prev => ({
                ...prev,
                [stepKey]: { ...prev[stepKey], [field]: value }
            }));
        }
    };

    const handleTravelerChange = (index, field, value) => {
        const updatedTravelers = formData.travelers.map((t, i) =>
            i === index ? { ...t, [field]: value } : t
        );
        setFormData(prev => ({ ...prev, travelers: updatedTravelers }));
    };

    const handleAddonChange = (addon) => {
        setFormData(prev => ({
            ...prev,
            addons: { ...prev.addons, [addon]: !prev.addons[addon] }
        }));
    };

    const handleFinalSubmit = (e) => {
        e.preventDefault();
        setBookingId(`AT-${Math.floor(Math.random() * 1000000)}`);
        setIsConfirmed(true);
        window.scrollTo(0, 0);
    };

    const StageWrapper = ({ num, title, children, isLast }) => (
        <div className="d-flex mb-0">
            <div className="d-flex flex-column align-items-center me-4" style={{ width: '40px' }}>
                <div className="rounded-circle bg-white border border-primary d-flex align-items-center justify-content-center fw-bold text-primary"
                    style={{ width: '40px', height: '40px', zIndex: 2, borderSize: '2px' }}>
                    {num}
                </div>
                {!isLast && (
                    <div className="flex-grow-1 border-start border-2 border-primary border-dotted my-2" style={{ width: '0' }}></div>
                )}
            </div>
            <div className="flex-grow-1 pb-5">
                <div className="card border-0 shadow-sm p-4 h-100">
                    <h4 className="mb-4">{title}</h4>
                    {children}
                </div>
            </div>
        </div>
    );

    if (isConfirmed) {
        return (
            <div className="container py-5 text-center">
                <div className="bg-white shadow p-5 rounded">
                    <div className="mb-4">
                        <div className="bg-success text-white d-inline-block rounded-circle p-4 shadow">
                            <i className="fa fa-check fa-4x"></i>
                        </div>
                    </div>
                    <h2 className="mb-3">Tabriklaymiz!</h2>
                    <h5 className="text-muted mb-4">Sizning sayohatingiz muvaffaqiyatli band qilindi.</h5>
                    <div className="bg-light p-3 rounded mb-4 d-inline-block px-5">
                        <span className="text-muted small d-block">BUYURTMA RAQAMI</span>
                        <span className="fw-bold h4">{bookingId}</span>
                    </div>
                    <p className="text-center mb-5">Barcha ma'lumotlar emailingizga yuborildi. Tez orada menejerimiz siz bilan bog'lanadi.</p>
                    <button className="btn btn-primary btn-lg rounded-pill px-5" onClick={() => navigate('/packages')}>Paketlar sahifasiga qaytish</button>
                </div>
            </div>
        );
    }

    return (
        <div className="container-fluid py-5 bg-light" style={{ minHeight: '100vh' }}>
            <div className="container">
                <div className="d-flex align-items-center justify-content-between mb-5">
                    <div className="d-flex align-items-center">
                        <div className="bg-primary text-white rounded p-2 me-3">
                            <i className="fa fa-shopping-cart"></i>
                        </div>
                        <h2 className="m-0">Buyurtma yaratish</h2>
                    </div>
                    <div className="badge bg-primary fs-5 p-3 rounded-pill shadow-sm">
                        Umumiy narx: ${totalPrice}
                    </div>
                </div>

                <div className="row justify-content-center">
                    <div className="col-lg-10">
                        <form onSubmit={handleFinalSubmit}>
                            {/* Stage 1: Travel Details */}
                            <StageWrapper num={1} title="Sayohat tafsilotlari">
                                <p className="text-muted mb-4">Qaysi sanada va necha kishi bo'lib sayohat qilmoqchisiz?</p>
                                <div className="row g-3">
                                    <div className="col-md-6">
                                        <label className="form-label fw-bold small text-muted">SAYOHAT SANASI</label>
                                        <input type="date" className="form-control form-control-lg border-light bg-light" required value={formData.travelDate} onChange={(e) => handleInputChange('direct', 'travelDate', e.target.value)} />
                                    </div>
                                    <div className="col-md-6">
                                        <label className="form-label fw-bold small text-muted">KATTALAR SONI</label>
                                        <input type="number" min="1" className="form-control form-control-lg border-light bg-light" value={formData.adults} onChange={(e) => handleInputChange('direct', 'adults', parseInt(e.target.value))} />
                                    </div>
                                    <div className="col-md-6">
                                        <label className="form-label fw-bold small text-muted">BOLALAR SONI</label>
                                        <input type="number" min="0" className="form-control form-control-lg border-light bg-light" value={formData.children} onChange={(e) => handleInputChange('direct', 'children', parseInt(e.target.value))} />
                                    </div>
                                    <div className="col-md-6">
                                        <label className="form-label fw-bold small text-muted">XONALAR SONI</label>
                                        <input type="number" min="1" className="form-control form-control-lg border-light bg-light" value={formData.rooms} onChange={(e) => handleInputChange('direct', 'rooms', parseInt(e.target.value))} />
                                    </div>
                                </div>
                            </StageWrapper>

                            {/* Stage 2: Package Info */}
                            <StageWrapper num={2} title="Paket ma'lumotlari">
                                <div className="bg-light p-4 rounded border-0 d-flex align-items-center">
                                    <img src={selectedPackage.image} alt={selectedPackage.title} className="rounded shadow-sm me-4" style={{ width: '150px', height: '100px', objectFit: 'cover' }} />
                                    <div>
                                        <h5 className="mb-1">{selectedPackage.title}</h5>
                                        <p className="text-muted small mb-2"><i className="fa fa-map-marker-alt text-primary me-2"></i> {selectedPackage.location}</p>
                                        <div className="d-flex gap-4 small fw-bold text-dark">
                                            <span><i className="fa fa-calendar-alt text-primary me-2"></i> {selectedPackage.duration}</span>
                                            <span><i className="fa fa-tag text-primary me-2"></i> ${selectedPackage.price} / kishi</span>
                                        </div>
                                    </div>
                                </div>
                            </StageWrapper>

                            {/* Stage 3: Additional Services */}
                            <StageWrapper num={3} title="Qo'shimcha xizmatlar">
                                <div className="row g-3">
                                    {[
                                        { id: 'transfer', label: 'Aeroport transferi ($50)', key: 'transfer' },
                                        { id: 'upgrade', label: 'Mehmonxonani yangilash ($100)', key: 'hotelUpgrade' },
                                        { id: 'guide', label: 'Gid xizmati ($80)', key: 'guide' },
                                        { id: 'meals', label: 'Ovqatlanish (Full board) ($60)', key: 'meals' },
                                        { id: 'insurance', label: "Sayohat sug'urtasi ($30)", key: 'insurance' }
                                    ].map((addon) => (
                                        <div className="col-md-6" key={addon.id}>
                                            <div className="form-check p-3 border rounded border-light bg-light h-100 d-flex align-items-center">
                                                <input className="form-check-input ms-0" type="checkbox" id={addon.id} checked={formData.addons[addon.key]} onChange={() => handleAddonChange(addon.key)} />
                                                <label className="form-check-label ms-3 fw-bold" htmlFor={addon.id}>{addon.label}</label>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </StageWrapper>

                            {/* Stage 4: Traveler Information */}
                            <StageWrapper num={4} title="Sayohatchilar ma'lumotlari">
                                {formData.travelers.map((traveler, index) => (
                                    <div key={index} className="mb-4 p-4 border-0 rounded bg-light border-start border-4 border-primary">
                                        <h6 className="text-primary mb-3 fw-bold text-uppercase"><i className="fa fa-user me-2"></i> {index + 1}-sayohatchi</h6>
                                        <div className="row g-3">
                                            <div className="col-md-6">
                                                <input type="text" className="form-control border-0 shadow-sm" placeholder="Ism" required value={traveler.firstName} onChange={(e) => handleTravelerChange(index, 'firstName', e.target.value)} />
                                            </div>
                                            <div className="col-md-6">
                                                <input type="text" className="form-control border-0 shadow-sm" placeholder="Familiya" required value={traveler.lastName} onChange={(e) => handleTravelerChange(index, 'lastName', e.target.value)} />
                                            </div>
                                            <div className="col-md-6">
                                                <input type="date" className="form-control border-0 shadow-sm" required value={traveler.dob} onChange={(e) => handleTravelerChange(index, 'dob', e.target.value)} />
                                            </div>
                                            <div className="col-md-6">
                                                <select className="form-select border-0 shadow-sm" value={traveler.gender} onChange={(e) => handleTravelerChange(index, 'gender', e.target.value)}>
                                                    <option value="Male">Erkak</option>
                                                    <option value="Female">Ayol</option>
                                                </select>
                                            </div>
                                            <div className="col-12">
                                                <input type="text" className="form-control border-0 shadow-sm" placeholder="Pasport raqami" required value={traveler.passport} onChange={(e) => handleTravelerChange(index, 'passport', e.target.value)} />
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </StageWrapper>

                            {/* Stage 5: Contact Information */}
                            <StageWrapper num={5} title="Bog'lanish ma'lumotlari">
                                <div className="row g-3">
                                    <div className="col-md-6">
                                        <input type="tel" className="form-control form-control-lg border-light bg-light" placeholder="Telefon raqami" required value={formData.contact.phone} onChange={(e) => handleInputChange('contact', 'phone', e.target.value)} />
                                    </div>
                                    <div className="col-md-6">
                                        <input type="email" className="form-control form-control-lg border-light bg-light" placeholder="Email manzili" required value={formData.contact.email} onChange={(e) => handleInputChange('contact', 'email', e.target.value)} />
                                    </div>
                                    <div className="col-12">
                                        <input type="text" className="form-control form-control-lg border-light bg-light" placeholder="Shahar / Davlat" required value={formData.contact.city} onChange={(e) => handleInputChange('contact', 'city', e.target.value)} />
                                    </div>
                                    <div className="col-12">
                                        <textarea className="form-control border-light bg-light" rows="3" placeholder="Maxsus so'rovlar (ixtiyoriy)" value={formData.contact.notes} onChange={(e) => handleInputChange('contact', 'notes', e.target.value)}></textarea>
                                    </div>
                                </div>
                            </StageWrapper>

                            {/* Stage 6: Review */}
                            <StageWrapper num={6} title="Buyurtmani tekshirish">
                                <div className="bg-light p-4 rounded text-dark border-0">
                                    <div className="row">
                                        <div className="col-md-6">
                                            <p className="mb-1 text-muted small fw-bold">SANANI BELGILANGAN:</p>
                                            <p className="fw-bold fs-5">{formData.travelDate || 'X'}</p>
                                            <p className="mb-1 text-muted small fw-bold">SAYOHATCHILAR:</p>
                                            <p className="fw-bold">{formData.adults}ta katta, {formData.children}ta bola</p>
                                        </div>
                                        <div className="col-md-6 ps-md-4 border-start">
                                            <p className="mb-1 text-muted small fw-bold">QO'SHIMCHA XIZMATLAR:</p>
                                            <p className="fw-bold text-truncate">{Object.entries(formData.addons).filter(([_, v]) => v).map(([k]) => k).join(', ') || "Yo'q"}</p>
                                            <h4 className="text-primary mt-3">Jami: ${totalPrice}</h4>
                                        </div>
                                    </div>
                                </div>
                            </StageWrapper>

                            {/* Stage 7: Payment & Submit */}
                            <StageWrapper num={7} title="To'lov va yakunlash" isLast={true}>
                                <div className="row g-4 align-items-center">
                                    <div className="col-md-7">
                                        <select className="form-select form-select-lg border-light bg-light mb-3" value={formData.paymentMethod} onChange={(e) => handleInputChange('direct', 'paymentMethod', e.target.value)}>
                                            <option value="Card"> Kredit / Debet karta</option>
                                            <option value="Bank"> Bank o'tkazmasi</option>
                                            <option value="Online"> Onlayn to'lov (Payme/Click)</option>
                                        </select>
                                        <div className="form-check shadow-none">
                                            <input className="form-check-input ms-0" type="checkbox" id="terms" required />
                                            <label className="form-check-label ms-3 fw-bold small" htmlFor="terms">Foydalanish shartlari va qonuniy qoidalarga roziman</label>
                                        </div>
                                    </div>
                                    <div className="col-md-5 text-end">
                                        <button type="submit" className="btn btn-primary btn-lg w-100 py-3 rounded shadow fw-bold">
                                            BUYURTMA BERISH <i className="fa fa-arrow-right ms-2"></i>
                                        </button>
                                    </div>
                                </div>
                            </StageWrapper>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default BandQilish;
