import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import Select from 'react-select';
import Slider from 'rc-slider';
import 'react-datepicker/dist/react-datepicker.css';
import 'rc-slider/assets/index.css';

const TourSearchForm = () => {
    // State for form fields
    const [departureCity, setDepartureCity] = useState(null);
    const [destination, setDestination] = useState(null);
    const [dateRange, setDateRange] = useState([null, null]);
    const [startDate, endDate] = dateRange;
    const [guests, setGuests] = useState({ adults: 2, children: 0, rooms: 1 });
    const [showGuestPopover, setShowGuestPopover] = useState(false);
    const [showAdvanced, setShowAdvanced] = useState(false);

    // Advanced Filters State
    const [priceRange, setPriceRange] = useState([100, 5000]);
    const [durationRange, setDurationRange] = useState([3, 14]);
    const [selectedRatings, setSelectedRatings] = useState([]);
    const [selectedMealPlans, setSelectedMealPlans] = useState([]);

    // Mock Data
    const departureOptions = [
        { value: "TAS", label: "üìç Tashkent" },
        { value: "SAM", label: "üìç Samarkand" },
        { value: "BUK", label: "üìç Bukhara" },
        { value: "BAK", label: "üá¶üáø Baku" },
        { value: "IST", label: "üáπüá∑ Istanbul" },
        { value: "DXB", label: "üá¶üá™ Dubai" }
    ];

    const destinationOptions = [
        { value: "UAE", label: "üá¶üá™ UAE" },
        { value: "TR", label: "üáπüá∑ Turkey" },
        { value: "TH", label: "üáπüá≠ Thailand" },
        { value: "MV", label: "üá≤üáª Maldives" },
        { value: "EG", label: "üá™üá¨ Egypt" }
    ];

    const ratingOptions = [
        { value: "5star", label: "‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê 5 Stars" },
        { value: "4star", label: "‚≠ê‚≠ê‚≠ê‚≠ê 4 Stars" },
        { value: "3star", label: "‚≠ê‚≠ê‚≠ê 3 Stars" }
    ];

    const mealPlanOptions = [
        { value: "AI", label: "üçΩÔ∏è All Inclusive" },
        { value: "FB", label: "üç± Full Board" },
        { value: "HB", label: "ü•ó Half Board" },
        { value: "BB", label: "‚òï Bed & Breakfast" }
    ];

    const toggleSelection = (value, currentList, setList) => {
        if (currentList.includes(value)) {
            setList(currentList.filter(item => item !== value));
        } else {
            setList([...currentList, value]);
        }
    };

    const handleSearch = (e) => {
        e.preventDefault();
        console.log("Searching with:", {
            departureCity,
            destination,
            dateRange,
            guests,
            priceRange,
            durationRange,
            selectedRatings,
            selectedMealPlans
        });
        alert("Search initiated! Check console for details.");
    };

    return (
        <form onSubmit={handleSearch}>
            {/* Primary Search Grid */}
            <div className="primary-search-grid">
                {/* From */}
                <div className="form-field">
                    <label className="field-label">Departure</label>
                    <div className="field-input-wrapper">
                        <Select
                            className="basic-single feature-input"
                            classNamePrefix="select"
                            isClearable
                            isSearchable
                            name="departure"
                            options={departureOptions}
                            placeholder="City of departure"
                            value={departureCity}
                            onChange={setDepartureCity}
                        />
                    </div>
                </div>

                {/* To */}
                <div className="form-field">
                    <label className="field-label">Destination</label>
                    <div className="field-input-wrapper">
                        <Select
                            className="basic-single feature-input"
                            classNamePrefix="select"
                            isClearable
                            isSearchable
                            isMulti
                            name="destination"
                            options={destinationOptions}
                            placeholder="Where do you want to go?"
                            value={destination}
                            onChange={setDestination}
                        />
                    </div>
                </div>

                {/* Dates */}
                <div className="form-field">
                    <label className="field-label">Dates</label>
                    <div className="field-input-wrapper">
                        <span className="field-icon">üìÖ</span>
                        <DatePicker
                            selectsRange={true}
                            startDate={startDate}
                            endDate={endDate}
                            onChange={(update) => setDateRange(update)}
                            className="feature-input"
                            placeholderText="Select date range"
                            monthsShown={2}
                            minDate={new Date()}
                        />
                    </div>
                </div>

                {/* Guests */}
                <div className="form-field position-relative">
                    <label className="field-label">Guests</label>
                    <div
                        className="field-input-wrapper cursor-pointer"
                        onClick={() => setShowGuestPopover(!showGuestPopover)}
                    >
                        <span className="field-icon">üë•</span>
                        <div className="feature-input d-flex align-items-center">
                            {guests.adults} Adults, {guests.children} Child
                        </div>
                    </div>

                    {showGuestPopover && (
                        <div className="position-absolute bg-white shadow rounded p-3 start-0 top-100 mt-2" style={{ zIndex: 100, minWidth: '250px' }}>
                            <div className="d-flex justify-content-between align-items-center mb-2">
                                <span>Adults</span>
                                <div>
                                    <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => setGuests({ ...guests, adults: Math.max(1, guests.adults - 1) })}>-</button>
                                    <span className="mx-2">{guests.adults}</span>
                                    <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => setGuests({ ...guests, adults: guests.adults + 1 })}>+</button>
                                </div>
                            </div>
                            <div className="d-flex justify-content-between align-items-center mb-2">
                                <span>Children</span>
                                <div>
                                    <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => setGuests({ ...guests, children: Math.max(0, guests.children - 1) })}>-</button>
                                    <span className="mx-2">{guests.children}</span>
                                    <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => setGuests({ ...guests, children: guests.children + 1 })}>+</button>
                                </div>
                            </div>
                            <div className="d-flex justify-content-between align-items-center">
                                <span>Rooms</span>
                                <div>
                                    <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => setGuests({ ...guests, rooms: Math.max(1, guests.rooms - 1) })}>-</button>
                                    <span className="mx-2">{guests.rooms}</span>
                                    <button type="button" className="btn btn-sm btn-outline-secondary" onClick={() => setGuests({ ...guests, rooms: guests.rooms + 1 })}>+</button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>

                {/* Search Button */}
                <button type="submit" className="search-btn">
                    üîç Search
                </button>
            </div>

            {/* Advanced Toggle */}
            <div className="advanced-toggle-wrapper">
                <button
                    type="button"
                    className="advanced-toggle"
                    onClick={() => setShowAdvanced(!showAdvanced)}
                >
                    ‚öôÔ∏è Advanced Filters {showAdvanced ? '‚ñ≤' : '‚ñº'}
                </button>
                {showAdvanced && (
                    <button
                        type="button"
                        className="text-muted btn btn-link text-decoration-none"
                        style={{ fontSize: '14px' }}
                        onClick={() => {
                            setPriceRange([100, 5000]);
                            setDurationRange([3, 14]);
                            setSelectedRatings([]);
                            setSelectedMealPlans([]);
                        }}
                    >
                        Reset All
                    </button>
                )}
            </div>

            {/* Advanced Filters */}
            {showAdvanced && (
                <div className="advanced-filters-grid animated fadeIn">
                    {/* Duration */}
                    <div className="filter-section">
                        <div className="filter-title">Duration: {durationRange[0]} - {durationRange[1]} nights</div>
                        <Slider
                            range
                            min={1}
                            max={30}
                            value={durationRange}
                            onChange={setDurationRange}
                            trackStyle={{ backgroundColor: '#9ACD32' }}
                            handleStyle={{ borderColor: '#9ACD32', backgroundColor: '#9ACD32' }}
                        />
                    </div>

                    {/* Price */}
                    <div className="filter-section">
                        <div className="filter-title">Price: ${priceRange[0]} - ${priceRange[1]}</div>
                        <Slider
                            range
                            min={0}
                            max={10000}
                            step={50}
                            value={priceRange}
                            onChange={setPriceRange}
                            trackStyle={{ backgroundColor: '#9ACD32' }}
                            handleStyle={{ borderColor: '#9ACD32', backgroundColor: '#9ACD32' }}
                        />
                    </div>

                    {/* Hotel Rating */}
                    <div className="filter-section">
                        <div className="filter-title">Hotel Rating</div>
                        <div className="checkbox-group">
                            {ratingOptions.map(option => (
                                <label key={option.value} className="checkbox-label">
                                    <input
                                        type="checkbox"
                                        checked={selectedRatings.includes(option.value)}
                                        onChange={() => toggleSelection(option.value, selectedRatings, setSelectedRatings)}
                                    />
                                    {option.label}
                                </label>
                            ))}
                        </div>
                    </div>

                    {/* Meal Plan */}
                    <div className="filter-section">
                        <div className="filter-title">Meal Plan</div>
                        <div className="chips-container">
                            {mealPlanOptions.map(option => (
                                <div
                                    key={option.value}
                                    className={`filter-chip ${selectedMealPlans.includes(option.value) ? 'active' : ''}`}
                                    onClick={() => toggleSelection(option.value, selectedMealPlans, setSelectedMealPlans)}
                                >
                                    {option.label}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </form>
    );
};

export default TourSearchForm;
